import { motion } from "streamui-deps";
import { FaCheck } from "streamui-deps";
import { IoIosClose } from "streamui-deps";
import PropTypes from "prop-types";

const plans = [
  {
    name: "Starter",
    price: "Free",
    description: "Basic components and regular updates.",
    features: ["10+ Components", "Basic Animations", "Light/Dark Mode", "All Starter Features"],
    notIncluded: ["Priority Support", "Component Requests", "Team Collaboration"],
    highlight: false,
  },
  {
    name: "Pro",
    price: "$9/mo",
    description: "Access all advanced components and premium support.",
    features: ["All Starter Features", "Animated Components", "Priority Support", "Component Requests"],
    notIncluded: ["Priority Support", "Component Requests", "Team Collaboration"],
    highlight: true,
  },
  {
    name: "Team",
    price: "$29/mo",
    description: "For teams and startups needing advanced support.",
    features: [
      "All Pro Features",
      "Team Collaboration",
      "Custom Branding",
      "Animated Components",
      "Priority Support",
      "Light/Dark Mode",
      "Component Requests",
    ],
    highlight: false,
  },
];

export function PricingCard({
  name,
  price,
  description,
  features,
  notIncluded,
  highlight,
}) {
  return (
    <motion.div
      whileHover={{ scale: 1.03 }}
      whileTap={{ scale: 0.98 }}
      className={`border rounded-xl p-6 shadow-sm bg-white dark:bg-neutral-900 ${
        highlight
          ? "border-blue-500 shadow-lg"
          : "border-gray-200 dark:border-neutral-700"
      }`}
    >
      <div className="flex justify-center">
        <h3 className="text-lg dark:text-black font-medium bg-green-500 mb-4 px-6 py-1 rounded-full">
          {name}
        </h3>
      </div>
      <p className="text-3xl font-bold mb-4">{price}</p>
      <p className="text-gray-600 dark:text-gray-400 mb-4">{description}</p>
      <ul className="text-sm text-left mb-6">
        {features.map((feature) => (
          <li key={feature} className="flex items-center mb-1">
            <span className="text-green-500 mr-2">
              <FaCheck />
            </span>{" "}
            {feature}
          </li>
        ))}
        {notIncluded &&
          notIncluded.map((item) => (
            <li key={item} className="flex items-center mb-1">
              <span className="text-red-500">
                <IoIosClose className="text-2xl font-semibold" />
              </span>
              {item}
            </li>
          ))}
      </ul>
      <button className="w-full py-2 rounded-md bg-black text-white dark:bg-white dark:text-black hover:opacity-90 transition">
        Get Started
      </button>
    </motion.div>
  );
}

PricingCard.propTypes = {
  name: PropTypes.string.isRequired,
  price: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  features: PropTypes.arrayOf(PropTypes.string).isRequired,
  notIncluded: PropTypes.arrayOf(PropTypes.string),
  highlight: PropTypes.bool,
};

export default function PricingSection() {
  return (
    <section className="py-16 bg-gray-50 dark:bg-neutral-950">
      <div className="max-w-5xl mx-auto px-4 text-center">
        <h2 className="text-3xl font-bold mb-4">Choose Your Plan</h2>
        <p className="text-gray-600 dark:text-gray-400 mb-12">
          Simple, transparent pricing for developers and indie makers.
        </p>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {plans.map((plan) => (
            <PricingCard
              key={plan.name}
              name={plan.name}
              price={plan.price}
              description={plan.description}
              features={plan.features}
              notIncluded={plan.notIncluded}
              highlight={plan.highlight}
            />
          ))}
        </div>
      </div>
    </section>
  );
}

"use client";
import Link from "next/link";
import Image from "next/image";
import { useState, useEffect, useMemo } from "react";
import { GiHamburgerMenu } from "streamui-deps";

export default function Navbar({
  logo = { text: "StreamUI", href: "/", image: null },
  links = [
    { label: "Home", href: "#home" },
    { label: "Features", href: "#features" },
    { label: "Reviews", href: "#Reviews" },
    { label: "FAQs", href: "#faqs" },
  ],
  mode = "light",
}) {
  const [isDark, setIsDark] = useState(mode === "dark");
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    setIsDark(mode === "dark");
  }, [mode]);

  const actions = useMemo(() => (
    <div className="flex gap-2">
      <button
        className={`px-4 py-1 rounded ring-1 ring-inset ${
          isDark
            ? "bg-white text-black ring-black"
            : "bg-black text-white ring-white"
        }`}
      >
        Log In
      </button>
      <button
        className={`px-4 py-1 rounded ring-1 ring-inset ${
          isDark
            ? "bg-black text-white ring-white"
            : "bg-white text-black ring-black"
        }`}
      >
        Sign Up
      </button>
    </div>
  ), [isDark]);

  return (
    <nav
      className={`relative max-w-4xl mx-auto rounded-xl w-[95%] flex flex-col md:flex-row items-center justify-between px-6 py-3 backdrop-blur-md shadow-lg overflow-hidden ${
        isDark ? "text-white" : "text-black"
      }`}
    >
      {/* Logo + Hamburger */}
      <div className="w-full flex items-center justify-between md:w-auto">
        <Link href={logo.href} className="flex items-center space-x-2 text-lg font-normal">
          {logo.image && <Image src={logo.image} alt={logo.text} width={32} height={32} />}
          {logo.text && <span>{logo.text}</span>}
        </Link>
        {/* Hamburger */}
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="md:hidden inline-flex items-center justify-center p-2 rounded"
        >
          <GiHamburgerMenu className={`${isDark ? "text-white" : "text-black"}`} />
        </button>
      </div>

      {/* Links + Actions */}
      <div
        className={`md:flex md:flex-row md:items-center md:space-x-4 md:w-auto w-full ${
          isOpen
            ? `flex flex-col rounded-b-xl shadow-md px-6 py-4 space-y-4 mt-2 backdrop-blur-md ${
                isDark ? "bg-black/70 text-white" : "bg-white/80 text-black"
              }`
            : "hidden md:flex"
        }`}
      >
        {links.map((link) => (
          <Link
            key={link.href}
            href={link.href}
            className={`hover:underline ${
              isDark ? "hover:text-neutral-300" : "hover:text-neutral-700"
            }`}
          >
            {link.label}
          </Link>
        ))}
        {/* Show actions inside hamburger on mobile */}
        <div className="flex gap-2 md:hidden">
          <button
            className={`px-4 py-1 rounded ring-1 ring-inset ${
              isDark
                ? "bg-white text-black ring-black"
                : "bg-black text-white ring-white"
            }`}
          >
            Log In
          </button>
          <button
            className={`px-4 py-1 rounded ring-1 ring-inset ${
              isDark
                ? "bg-black text-white dark:ring-white"
                : "bg-white text-black ring-black"
            }`}
          >
            Sign Up
          </button>
        </div>
      </div>

      {/* Always visible actions on desktop */}
      <div className="hidden md:flex">{actions}</div>

      {/* Glass overlay */}
      <div className="pointer-events-none absolute inset-0 rounded-xl border border-white/20">
        <div
          className={`absolute inset-0 rounded-xl bg-gradient-to-br ${
            isDark ? "from-white/10 to-transparent" : "from-neutral-300/30 to-transparent"
          }`}
        ></div>
      </div>
    </nav>
  );
}

